CREATE function expiry_value_prc
                                   ( var_symbol in varchar2, 
                                   l_last_trade_dt in date,
                                   l_pos_movement_days in number ,
                                   l_neg_movement_days in number,
                                   total_close_price_positive in number,
                                   total_close_price_negative in number,
                                   l_ttl_trd_qnty_tot in number,
                                   l_num_days         in number,
                                   l_ttl_trd_qnty_pos in number,
                                   l_ttl_trd_qnty_neg in number,
                                   expariy_month in varchar2)
                                   
 return number IS
 l_option_last_dt date;
 l_trade_days_cnt number;
 l_pos_days_upto_expiry number;
 l_neg_days_upto_expiry number;
 last_close_price_n number;
 l_exp_price_upto_expiry NUMBER;
 l_exp_trd_quan_upto_exp NUMBER;
 l_exp_trd_quan_upto_exp_pos NUMBER;
 l_exp_trd_quan_upto_exp_neg NUMBER;
 
BEGIN
select TRADE_DATE_D into l_option_last_dt from TRADE_DATE_BSE_NSE 
where  OPTION_EXPARIY_V='Y' AND TO_CHAR(TRADE_DATE_D,'MMYY')=expariy_month;
select count(*) into l_trade_days_cnt from  TRADE_DATE_BSE_NSE  
where TRADE_DATE_D between l_last_trade_dt  and l_option_last_dt 
and TRADE_HOLIDAY_DAY_V IS NULL;
l_trade_days_cnt:=l_trade_days_cnt-1;
 
l_pos_days_upto_expiry:= l_trade_days_cnt*((l_pos_movement_days)/(l_pos_movement_days+l_neg_movement_days));
l_neg_days_upto_expiry:=l_trade_days_cnt-l_pos_days_upto_expiry;

 
 begin
 select close_price into last_close_price_n from daily_nse_all 
 where date1=l_last_trade_dt and symbol=var_symbol  AND SERIES='EQ';
 exception
 when no_data_found then 
 dbms_output.put_line('The last close price symobl is not found'||var_symbol);
 end;
 
 ----TOTAL EXCEPTED TRADED QUANITY TRADING QUANTITY BEFORE EXPIRY .
 l_exp_trd_quan_upto_exp:=(l_ttl_trd_qnty_tot/l_num_days)*l_trade_days_cnt;
