        TEST_E1_AR(J+1).DATE1 , ---DATE 
        TEST_E1_AR(J+1).DELIV_PER); ---Deliver PCT
   ELSE 
   
    SUCCESS_FLAG_TQ:=1;
   DBMS_OUTPUT.PUT_LINE('The line number ----->5'); 
    EXIT; 
    
   END IF;  
   
    
   ELSE 
   
   SUCCESS_FLAG_PR:=1;
   DBMS_OUTPUT.PUT_LINE('The line number ----->6');
   EXIT;
   
   END IF;
   
   
   
   
   END LOOP;
   
   
   
   IF  SUCCESS_FLAG_PR=0 AND   SUCCESS_FLAG_TQ=0 THEN 
  
 FORALL i in TEST_E1_AR.first .. TEST_E1_AR.last
 INSERT INTO TEST_E2 VALUES TEST_E1_AR(i); 
 DBMS_OUTPUT.PUT_LINE('The line number ----->7');
 
 
  COMMIT; 
   END IF ;
  
    
  END LOOP;
  
 EXCEPTION 
 
 WHEN OTHERS THEN 
 
  DBMS_OUTPUT.PUT_LINE('The error in program '||sqlerrm);
 
 
  
  END;
