  DECLARE 
  CURSOR C1 IS SELECT DISTINCT SYMBOL FROM nse_master  ;
  TYPE  DAILY_NSE_ALL_T IS TABLE OF DAILY_NSE_ALL%ROWTYPE INDEX BY PLS_INTEGER;
  DAILY_NSE_ALL_AR DAILY_NSE_ALL_T;
  VAR_CLOSE_PRICE DAILY_NSE_ALL.CLOSE_PRICE%TYPE :=0;
  VAR_TTL_TRD_QNTY DAILY_NSE_ALL.TTL_TRD_QNTY%TYPE:=0;
  VAR_POS_TRD_QNTY DAILY_NSE_ALL.TTL_TRD_QNTY%TYPE:=0;
  VAR_NEG_TRD_QNTY  DAILY_NSE_ALL.TTL_TRD_QNTY%TYPE:=0;
  VAR_POS_DIFF NUMBER;
  VAR_NEG_DIFF NUMBER;
  VAR_NEG_DIFF_PCT NUMBER;
  VAR_POS_DIFF_PCT NUMBER;
  L_CNT NUMBER;
  BEGIN
  FOR I IN C1 LOOP 
  
  SELECT COUNT(*) INTO L_CNT FROM DAILY_NSE_ALL WHERE 
  DATE1 >=l_date1 AND  SYMBOL=I.SYMBOL AND SERIES='EQ' ORDER BY DATE1;
  IF L_CNT >0 THEN
  BEGIN  
  SELECT * BULK COLLECT INTO DAILY_NSE_ALL_AR  FROM DAILY_NSE_ALL WHERE 
  DATE1 >=l_date1 AND  SYMBOL=I.SYMBOL AND SERIES='EQ' ORDER BY DATE1;
  VAR_CLOSE_PRICE :=0;
  VAR_TTL_TRD_QNTY :=0;
  FOR J IN 1 .. DAILY_NSE_ALL_AR.COUNT LOOP
  VAR_TTL_TRD_QNTY:= VAR_TTL_TRD_QNTY+DAILY_NSE_ALL_AR(J).TTL_TRD_QNTY; 
  END LOOP;
  VAR_POS_DIFF:=1;
  VAR_NEG_DIFF:=1;
  VAR_POS_TRD_QNTY:=1;
  VAR_NEG_TRD_QNTY:=1;
  FOR J IN 2 .. DAILY_NSE_ALL_AR.COUNT LOOP
  IF DAILY_NSE_ALL_AR(J-1).CLOSE_PRICE - DAILY_NSE_ALL_AR(J).CLOSE_PRICE >0 THEN
  VAR_POS_TRD_QNTY:= VAR_POS_TRD_QNTY+DAILY_NSE_ALL_AR(J).TTL_TRD_QNTY; 
  VAR_POS_DIFF:= VAR_POS_DIFF+(DAILY_NSE_ALL_AR(J-1).CLOSE_PRICE - DAILY_NSE_ALL_AR(J).CLOSE_PRICE);
  ELSE
  VAR_NEG_TRD_QNTY:= VAR_POS_TRD_QNTY+DAILY_NSE_ALL_AR(J).TTL_TRD_QNTY; 
  VAR_NEG_DIFF:=VAR_NEG_DIFF+ ABS(DAILY_NSE_ALL_AR(J-1).CLOSE_PRICE - DAILY_NSE_ALL_AR(J).CLOSE_PRICE);
  END IF;
  END LOOP;
  VAR_POS_DIFF_PCT:= ROUND(VAR_POS_TRD_QNTY/VAR_TTL_TRD_QNTY,2);
  VAR_NEG_DIFF_PCT:=ROUND(1-VAR_POS_DIFF_PCT,2);
  --VAR_POS_WT_PRICE_N:=VAR_POS_DIFF/VAR_POS_TRD_QNTY;
  --VAR_POS_WT_PRICE_N:=VAR_NEG_DIFF/VAR_NEG_TRD_QNTY
  
  VAR_CLOSE_PRICE:=DAILY_NSE_ALL_AR(1).CLOSE_PRICE-DAILY_NSE_ALL_AR(DAILY_NSE_ALL_AR.COUNT).CLOSE_PRICE;
  UPDATE SYMBOL_PERMIN_VOL SET AVG_WT_PRC_TRAN=VAR_CLOSE_PRICE/VAR_TTL_TRD_QNTY ,
  POS_DIFF_PCT=VAR_POS_DIFF_PCT, NEG_DIFF_PCT=VAR_NEG_DIFF_PCT, AVG_WT_PRC_TRAN_P=NVL(VAR_POS_DIFF/VAR_POS_TRD_QNTY,0), 
  AVG_WT_PRC_TRAN_N=NVL(VAR_NEG_DIFF/VAR_NEG_TRD_QNTY,0)
  WHERE 
  SYMBOL=I.SYMBOL ;
  
  COMMIT;
  
  EXCEPTION
  WHEN OTHERS THEN 
  DBMS_OUTPUT.PUT_LINE('The sysmbol is '||i.symbol);
  DBMS_OUTPUT.PUT_LINE('The total close price diff '||VAR_CLOSE_PRICE); 
  DBMS_OUTPUT.PUT_LINE('The total traded qunantity '||VAR_TTL_TRD_QNTY);
  DBMS_OUTPUT.PUT_LINE('The total post close diff '||VAR_POS_DIFF);
  DBMS_OUTPUT.PUT_LINE('The total postive close diff '||VAR_POS_TRD_QNTY);
  DBMS_OUTPUT.PUT_LINE('The total posttive traded quantity '||VAR_POS_TRD_QNTY);
  DBMS_OUTPUT.PUT_LINE('The total neg close diff: '||VAR_NEG_DIFF);
  DBMS_OUTPUT.PUT_LINE('The total neg traded quantity: '||VAR_NEG_TRD_QNTY);
  END;
  END IF;
  END LOOP;

  END ;
  
  
