    no_sms         EXCEPTION;
     ---select sms_trg_flg_v INTO l_sms_trg_flg_v from FIVEDAYS_AVG_TBL ;
   v_error_message VARCHAR2(1000);
   v_error_point VARCHAR2(20); 
BEGIN
    ----------------------------------------------------------------------------
