    ----------------------------------------------------------------------------
    v_error_point:='1.1';
    v_error_message:='Error calling in fivedays_avg_tbl from SENDSMS';
    SELECT /*+ result_cache */   sms_trg_flg_v INTO l_sms_trg_flg_v 
    FROM fivedays_avg_tbl WHERE symbol=vSymbol ;
    
   
      
      IF  l_module_force_sms_v='N' AND  l_sms_trg_flg_v='N' THEN 
       
       
       v_error_point:='1.2';
       v_error_message:='Before no_sms trigger in  from SENDSMS';
       
       RAISE no_sms;
       
      END IF; 
    
       v_error_point:='1.3';
