    ----------------------------------------------------------------------------
    -- Send SMS through the Spring Edge SMS service.
    ----------------------------------------------------------------------------
   
       v_error_point:='1.4';
       v_error_message:='Before calling  Utl_Http.begin_request in  from SENDSMS';
     vRequest := Utl_Http.begin_request
                  ( url    => 'http://instantalerts.co/api/web/send/'
                  , method => 'POST'
                  );
       v_error_point:='1.5';
       v_error_message:='Before calling  Utl_Http.set_header in  from SENDSMS';            
    Utl_Http.set_header
      ( r     => vRequest
      , name  => 'Content-Type'
      , value => 'application/x-www-form-urlencoded'
      );
     v_error_point:='1.6';
     v_error_message:='Before calling  Utl_Http.set_header in  from SENDSMS'; 
    Utl_Http.set_header
      ( r     => vRequest
      , name  => 'Content-Length'
      , value => LENGTH(vPostText)
      );
      
      v_error_point:='1.7';
      v_error_message:='Before calling  Utl_Http.write_text in  from SENDSMS';
     
    Utl_Http.write_text
      ( r    => vRequest
      , data => vPostText
      );
      v_error_point:='1.8';
      v_error_message:='Before calling  Utl_Http.get_response in  from SENDSMS'; 
   vResponse := Utl_Http.get_response(vRequest);
    
    IF vResponse.status_code = '200'
    THEN
        v_error_point:='1.9';
        v_error_message:='Before calling  Utl_Http.read_text in  from SENDSMS'; 
        Utl_Http.read_text(vResponse, vResponseText);
    ELSE
        vErrorText := 'HTTP status: '||vResponse.status_code||'-'||vResponse.reason_phrase;
    END IF;
    --
        v_error_point:='1.10';
        v_error_message:='Before calling  Utl_Http.end_response in  from SENDSMS';
       Utl_Http.end_response(vResponse);
    --
    IF vErrorText IS NOT NULL
    THEN
        RAISE_APPLICATION_ERROR(-20001, 'Sending SMS failed with '||vErrorText);
         v_error_point:='1.11';
        v_error_message:='Before inserting send_sms_symbol in  from SENDSMS';
    insert into  send_sms_symbol ( symbol , remarks_v , send_sms_flg  , sms_timestamp, BUYER_SELLER_PER_LAKH_N,short_long_flag_v,lastprice_n  ) 
