   EQUITY_ERROR_PRC  (  vSymbol,SYSDATE,TO_CHAR(SYSDATE,'HH24:MI'),NULL,NULL,SUBSTR(v_error_point||'~'||v_error_message||SQLERRM,1,200));
   
    
