CREATE PROCEDURE sendSMSCLIENT
  ( pRecipient IN VARCHAR2, pBody      IN VARCHAR2  )
IS
    SPRINGEDGE_APIKEY CONSTANT VARCHAR2(40) := '463802iot09oumgvam854q19fs82bdt2rd';
    SPRINGEDGE_SENDER CONSTANT VARCHAR2(40) := 'DASRAS';
---
    --
    vRequest      Utl_Http.req;
    vPostText     VARCHAR2(500);
    vResponse     Utl_Http.resp;
    vResponseText VARCHAR2(2000);
    vErrorText    VARCHAR2(200);
BEGIN
    ----------------------------------------------------------------------------
    -- data to send.
    -- Get api key from http://springedge.com/developers...
    ----------------------------------------------------------------------------
    vPostText :=
      'apikey=' ||Utl_Url.escape(SPRINGEDGE_APIKEY, TRUE)||CHR(38)||
      'sender=' ||Utl_Url.escape(SPRINGEDGE_SENDER, TRUE)||CHR(38)||
      'to='||Utl_Url.escape(pRecipient,       TRUE)||CHR(38)||
      'message='     ||Utl_Url.escape(pBody,            TRUE);
    ----------------------------------------------------------------------------
    -- Send SMS through the Spring Edge SMS service.
    ----------------------------------------------------------------------------
    vRequest := Utl_Http.begin_request
                  ( url    => 'http://instantalerts.co/api/web/send/'
                  , method => 'POST'
                  );
    Utl_Http.set_header
      ( r     => vRequest
      , name  => 'Content-Type'
      , value => 'application/x-www-form-urlencoded'
      );
    Utl_Http.set_header
      ( r     => vRequest
      , name  => 'Content-Length'
      , value => LENGTH(vPostText)
      );
    Utl_Http.write_text
      ( r    => vRequest
      , data => vPostText
      );
    vResponse := Utl_Http.get_response(vRequest);
    IF vResponse.status_code = '200'
    THEN
        Utl_Http.read_text(vResponse, vResponseText);
    ELSE
        vErrorText := 'HTTP status: '||vResponse.status_code||'-'||vResponse.reason_phrase;
    END IF;
    --
    Utl_Http.end_response(vResponse);
    --
    IF vErrorText IS NOT NULL
    THEN
        RAISE_APPLICATION_ERROR(-20001, 'Sending SMS failed with '||vErrorText);
        EQUITY_ERROR_PRC  ( 'sms',SYSDATE,TO_CHAR(SYSDATE,'HH24:MI'),NULL,NULL,SUBSTR(vErrorText||'~'||SQLERRM,1,200));
    ELSE 
     NULL;
    END IF;
END ;
