CREATE PROCEDURE short_long_flag_mod_prc as
CURSOR C1 IS SELECT DISTINCT SYMBOL FROM daily_nse_movement_trans2504;
TYPE daily_nse_movement_trans_t IS TABLE OF daily_nse_movement_trans2504%ROWTYPE INDEX BY PLS_INTEGER ;
TEMP_NSE_TRANS_AR daily_nse_movement_trans_T;
BEGIN

FOR I IN C1 LOOP 
SELECT * BULK COLLECT INTO TEMP_NSE_TRANS_AR 
FROM daily_nse_movement_trans2504 WHERE SYMBOL =I.SYMBOL  ORDER BY DAILY_BATCH_SEQ_N;

