CREATE PROCEDURE UPT_BUY_SELL_PERLAKH_PRC AS
 CURSOR C1 IS SELECT DISTINCT SYMBOL ,TRUNC(LAST_UPDATE_DT) LAST_UPDATE_TRUNC  FROM DAILY_NSE_MOVEMENT_TRANS_HIST 
 WHERE BUYER_SELLER_PER_LAKH_N IS NULL AND LAST_AVG_PRICE_N IS NOT NULL ORDER BY 2;
 TYPE DAILY_NSE_MOVEMENT_HIST_T IS TABLE OF DAILY_NSE_MOVEMENT_TRANS_HISTB%ROWTYPE INDEX BY PLS_INTEGER;
 DAILY_NSE_MOVEMENT_AR  DAILY_NSE_MOVEMENT_HIST_T;
 BEGIN
 FOR  I IN C1 LOOP 
 SELECT * BULK COLLECT INTO DAILY_NSE_MOVEMENT_AR
 FROM DAILY_NSE_MOVEMENT_TRANS_HISTB  WHERE SYMBOL=I.SYMBOL AND TRUNC(LAST_UPDATE_DT)=I.LAST_UPDATE_TRUNC 
 ORDER BY LAST_UPDATE_DT;
  FOR J IN 1 .. DAILY_NSE_MOVEMENT_AR.COUNT  LOOP 
  IF DAILY_NSE_MOVEMENT_AR(J).DAILY_BATCH_SEQ_N=1 THEN 
  update DAILY_NSE_MOVEMENT_TRANS_HISTB set  BUYER_SELLER_PER_LAKH_N=buy_sell_cnt_perlakh_fnc(DAILY_NSE_MOVEMENT_AR(J).lastPrice_n,DAILY_NSE_MOVEMENT_AR(J).last_avg_price_n,DAILY_NSE_MOVEMENT_AR(J).totalTradedVol_n,
  0) where SYMBOL=I.SYMBOL AND TRUNC(LAST_UPDATE_DT)=I.LAST_UPDATE_TRUNC AND DAILY_BATCH_SEQ_N=1;
  ELSE 
   IF DAILY_NSE_MOVEMENT_AR(J).totalTradedVol_n >DAILY_NSE_MOVEMENT_AR(J-1).totalTradedVol_n THEN
  update DAILY_NSE_MOVEMENT_TRANS_HISTB set  BUYER_SELLER_PER_LAKH_N=buy_sell_cnt_perlakh_fnc(DAILY_NSE_MOVEMENT_AR(J).lastPrice_n,DAILY_NSE_MOVEMENT_AR(J).last_avg_price_n,DAILY_NSE_MOVEMENT_AR(J).totalTradedVol_n,
  DAILY_NSE_MOVEMENT_AR(J-1).totalTradedVol_n) where SYMBOL=I.SYMBOL AND TRUNC(LAST_UPDATE_DT)=I.LAST_UPDATE_TRUNC AND 
  DAILY_BATCH_SEQ_N=DAILY_NSE_MOVEMENT_AR(J-1).DAILY_BATCH_SEQ_N;
  END IF;
  END IF;
  END LOOP;
  COMMIT;
  END LOOP;
  COMMIT;
  END;
/
CREATE procedure write_file  is
fhandle  utl_file.file_type;
l_location     VARCHAR2(100) := 'EQUITY_DIR';
l_filename     VARCHAR2(100) := 'rest.bat';

cursor c1 is select path_v from fno_master_t where rownum <10;
begin 
utl_file.fremove(l_location,l_filename);
fhandle := UTL_FILE.FOPEN(l_location, l_filename, 'w');

for i in c1 loop 
UTL_FILE.PUT_LINE(fhandle,i.path_v);
end loop;
UTL_FILE.FCLOSE(fhandle);
exception when others then
DBMS_OUTPUT.PUT_LINE('SQLERRM: ' || SQLERRM);
end;
