CREATE  FORCE VIEW "EQUITY"."NIFTY_L" ("BUYER_SELLER", "SYMBOL", "LAST_UPDATE_TIME", "DAILY_BATCH_SEQ_N", "BUY_SIGNAL_VALUE_N", "LAST_AVG_PRICE_N", "LASTPRICE_N", "PRICE_DIF") AS 
  SELECT  a.buyer_seller_per_lakh_n,a.symbol,a.last_update_time,a.daily_batch_seq_n,a.buy_signal_value_n ,
          last_avg_price_n,lastprice_n,a.last_bat_price_diff price_diff 
FROM daily_nse_movement_trans A,fivedays_avg_tbl B WHERE short_long_flag_v='L' AND a.symbol=b.symbol  
and last_update_dt BETWEEN TRUNC(SYSDATE) AND SYSDATE AND  a.totaltradedvol_n>b.pct5_trans_tot 
ORDER BY last_update_dt DESC;
CREATE  FORCE VIEW "EQUITY"."NIFTY_S" ("SELLER_BUYER", "SYMBOL", "LAST_UPDATE_TIME", "DAILY_BATCH_SEQ_N", "BUY_SIGNAL_VALUE_N", "LAST_AVG_PRICE_N", "LASTPRICE_N", "PRICE_DIF") AS 
  SELECT buyer_seller_per_lakh_n, a.symbol,a.last_update_time,a.daily_batch_seq_n,a.buy_signal_value_n , 
  last_avg_price_n,lastprice_n,a.last_bat_price_diff price_diff FROM daily_nse_movement_trans a,fivedays_avg_tbl b WHERE short_long_flag_v='S' 
  AND a.symbol=b.symboL  
  AND last_update_dt BETWEEN TRUNC(SYSDATE) AND SYSDATE AND  a.symbol=b.symbol AND a.totaltradedvol_n >b.pct5_trans_tot
  ORDER BY last_update_dt desc;
CREATE  FORCE VIEW "EQUITY"."NIFTY_VOLSHOCKER" ("VOLUME5PCT", "SYMBOL", "LAST_UPDATE_TIME", "DAILY_BATCH_SEQ_N", "BUY_SIGNAL_VALUE_N", "LAST_AVG_PRICE_N", "LASTPRICE_N", "PRICE_DIF") AS 
  SELECT A.TOTALTRADEDVOL_N/B.PCT5_TRANS_TOT,A.SYMBOL,A.last_update_time,A.DAILY_BATCH_SEQ_N,A.BUY_SIGNAL_VALUE_N, 
LAST_AVG_PRICE_N,LASTPRICE_N,A.last_bat_price_diff price_diff FROM daily_nse_movement_trans A,fivedays_avg_tbl B WHERE  A.SYMBOL=B.SYMBOL  
and last_update_dt between trunc(sysdate) and sysdate and SHORT_LONG_FLAG_V='V' AND DAILY_BATCH_SEQ_N=1
ORDER BY LAST_UPDATE_DT DESC,BUY_SIGNAL_VALUE_N DESC;
CREATE  FORCE VIEW "EQUITY"."nifty_v" ("VOLUME5PCT", "SYMBOL", "LAST_UPDATE_TIME", "DAILY_BATCH_SEQ_N", "BUY_SIGNAL_VALUE_N", "LAST_AVG_PRICE_N", "LASTPRICE_N", "PRICE_DIF") AS 
  SELECT a.totaltradedvol_n/b.pct5_trans_tot VOLUME5PCT,a.symbol,a.last_update_time,a.daily_batch_seq_n,a.buy_signal_value_n, 
last_avg_price_n,lastprice_n,a.last_bat_price_diff price_diff FROM daily_nse_movement_trans a,fivedays_avg_tbl b WHERE  a.symbol=b.symbol(+)  
AND last_update_dt BETWEEN TRUNC(SYSDATE) AND SYSDATE AND short_long_flag_v='V' AND daily_batch_seq_n=1
ORDER BY a.totaltradedvol_n/b.pct5_trans_tot desc;
CREATE  FORCE VIEW "EQUITY"."BUY_SELL_SHORT_VW" ("WEIGHT_N", "RATIO_F_N", "RATIO_F_V", "CUL_WEIGHT_N", "HIGH_PRC", "LOW_PRC", "AVG_PRC", "SYMBOL", "OPEN_PRICE", "PREV_CLOSE", "LASTPRICE_N", "SELLER", "DAILY_BATCH_SEQ_N", "LAST_UPDATE_DT_TIME") AS 
  SELECT WEIGHT_N, RATIO_F_N, RATIO_F_V, CUL_WEIGHT_N, HIGH_PRC, LOW_PRC, AVG_PRC, A.SYMBOL, OPEN_PRICE, 
PREV_CLOSE ,LASTPRICE_N,BUYER_SELLER_PER_LAKH_N SELLER,DAILY_BATCH_SEQ_N,TO_CHAR(LAST_UPDATE_DT,'DD-MON-YY HH24:MI') LAST_UPDATE_DT_TIME
FROM  DAILY_NSE_MOVEMENT_TRANS A, FIVEDAYS_AVG_TBL B WHERE A.SYMBOL=B.SYMBOL AND A.LAST_AVG_PRICE_N > A.LASTPRICE_N 
AND A.LASTPRICE_N<B.PREV_CLOSE  AND OPEN_PRICE < PREV_CLOSE 
ORDER BY CUL_WEIGHT_N DESC;
CREATE  FORCE VIEW "EQUITY"."BUY_SELL_SHORT_1_VW" ("WEIGHT_N", "RATIO_F_N", "RATIO_F_V", "CUL_WEIGHT_N", "HIGH_PRC", "LOW_PRC", "AVG_PRC", "SYMBOL", "OPEN_PRICE", "PREV_CLOSE", "LASTPRICE_N", "SELLER", "DAILY_BATCH_SEQ_N", "LAST_UPDATE_DT_TIME") AS 
  SELECT WEIGHT_N, RATIO_F_N, RATIO_F_V, CUL_WEIGHT_N, HIGH_PRC, LOW_PRC, AVG_PRC, A.SYMBOL, OPEN_PRICE, 
PREV_CLOSE ,LASTPRICE_N,BUYER_SELLER_PER_LAKH_N SELLER,DAILY_BATCH_SEQ_N,TO_CHAR(LAST_UPDATE_DT,'DD-MON-YY HH24:MI') LAST_UPDATE_DT_TIME
FROM  DAILY_NSE_MOVEMENT_TRANS A, FIVEDAYS_AVG_TBL B WHERE A.SYMBOL=B.SYMBOL AND A.LAST_AVG_PRICE_N > A.LASTPRICE_N 
AND A.LASTPRICE_N<B.PREV_CLOSE 
ORDER BY CUL_WEIGHT_N DESC;
CREATE  FORCE VIEW "EQUITY"."BUY_SELL_VW_MAX_EQ_OPEN" ("PCT", "LAST_PCT", "WR", "WEIGHT_N", "CUL_WEIGHT_N", "RATIO_F_N", "RATIO_F_V", "OPEN_PRICE", "MAX_PRICE_N", "MIN_PRICE_N", "PREV_CLOSE", "LASTPRICE_N", "LAST_AVG_PRICE_N", "BUYER_SELLER", "SYMBOL", "LAST_UPDATE_DT_TIME", "DAILY_BATCH_SEQ_N", "SHORT_LONG_FLAG_V", "SHORT_LONG_VIR_FLAG_V", "BUYER_SELLER_PER_LAKH_N", "PCT5_TRANS_TOT", "TOTALTRADEDVOL_N") AS 
  SELECT trunc(((OPEN_PRICE-PREV_CLOSE)/PREV_CLOSE)*100,2) pct, trunc(((LASTPRICE_N-PREV_CLOSE)/PREV_CLOSE)*100,2) LAST_pct,
  TRUNC((WEIGHT_N)/(BUYER_SELLER_PER_LAKH_N)) WR, WEIGHT_N,CUL_WEIGHT_N,
RATIO_F_N,
RATIO_F_V,OPEN_PRICE,MAX_PRICE_N,MIN_PRICE_N, PREV_CLOSE ,LASTPRICE_N,LAST_AVG_PRICE_N, 
                 CASE WHEN LAST_AVG_PRICE_N > LASTPRICE_N THEN 
                 'SELLER--->'||BUYER_SELLER_PER_LAKH_N
                 ELSE 
                 'BUYER--->'||BUYER_SELLER_PER_LAKH_N
                 END   BUYER_SELLER,  A.SYMBOL, TO_CHAR(LAST_UPDATE_DT,'DD-MON-YY HH24:MI') LAST_UPDATE_DT_TIME,  
                 DAILY_BATCH_SEQ_N, SHORT_LONG_FLAG_V, SHORT_LONG_VIR_FLAG_V,BUYER_SELLER_PER_LAKH_N,B.PCT5_TRANS_TOT,TOTALTRADEDVOL_N
FROM DAILY_NSE_MOVEMENT_TRANS A, FIVEDAYS_AVG_TBL B WHERE  
 BUYER_SELLER_PER_LAKH_N>0 AND BUYER_SELLER_PER_LAKH_N IS NOT NULL AND 
A.SYMBOL=B.SYMBOL AND OPEN_PRICE=MAX_PRICE_N
order by  5 desc,4 desc;
CREATE  FORCE VIEW "EQUITY"."BUY_SELL_VW_MIN_EQ_OPEN" ("PCT", "LAST_PCT", "WR", "WEIGHT_N", "CUL_WEIGHT_N", "RATIO_F_N", "RATIO_F_V", "OPEN_PRICE", "MAX_PRICE_N", "MIN_PRICE_N", "PREV_CLOSE", "LASTPRICE_N", "LAST_AVG_PRICE_N", "BUYER_SELLER", "SYMBOL", "LAST_UPDATE_DT_TIME", "DAILY_BATCH_SEQ_N", "SHORT_LONG_FLAG_V", "SHORT_LONG_VIR_FLAG_V", "BUYER_SELLER_PER_LAKH_N", "PCT5_TRANS_TOT", "TOTALTRADEDVOL_N") AS 
