  TRUNC((WEIGHT_N)/(BUYER_SELLER_PER_LAKH_N)) WR, WEIGHT_N,CUL_WEIGHT_N,
RATIO_F_N,
RATIO_F_V,OPEN_PRICE,MAX_PRICE_N,MIN_PRICE_N, PREV_CLOSE ,LASTPRICE_N,LAST_AVG_PRICE_N, 
                 CASE WHEN LAST_AVG_PRICE_N > LASTPRICE_N THEN 
                 'SELLER--->'||BUYER_SELLER_PER_LAKH_N
