FROM DAILY_NSE_MOVEMENT_TRANS A, FIVEDAYS_AVG_TBL B WHERE  
 BUYER_SELLER_PER_LAKH_N>0 AND BUYER_SELLER_PER_LAKH_N IS NOT NULL AND 
A.SYMBOL=B.SYMBOL AND OPEN_PRICE=MIN_PRICE_N
