CREATE  FORCE VIEW "EQUITY"."BUY_SELL_VW_H" ("PCT", "LAST_PCT", "WR", "WEIGHT_N", "CUL_WEIGHT_N", "PCT_W", "RATIO_F_V", "OPEN_PRICE", "MAX_PRICE_N", "MIN_PRICE_N", "PREV_CLOSE", "LASTPRICE_N", "SL_N", "SPEED", "B_SL_N", "S_SL_N", "LAST_AVG_PRICE_N", "BUYER_SELLER", "SYMBOL", "LAST_UPDATE_DT_TIME", "SEQ_N", "SHORT_LONG_FLAG_V", "SHORT_LONG_VIR_FLAG_V", "BUYER_SELLER_PER_LAKH_N", "PCT5_TRANS_TOT", "TOTALTRADEDVOL_N") AS 
  SELECT PCT, LAST_PCT, WR, WEIGHT_N, CUL_WEIGHT_N,PCT_W, RATIO_F_V, OPEN_PRICE, MAX_PRICE_N, MIN_PRICE_N, PREV_CLOSE, LASTPRICE_N, SL_N,SPEED, 
 LASTPRICE_N-SL_N B_SL_N , LASTPRICE_N+ SL_N S_SL_N,LAST_AVG_PRICE_N, BUYER_SELLER, SYMBOL, LAST_UPDATE_DT_TIME, SEQ_N, SHORT_LONG_FLAG_V, SHORT_LONG_VIR_FLAG_V, BUYER_SELLER_PER_LAKH_N, PCT5_TRANS_TOT, TOTALTRADEDVOL_N FROM  
 (SELECT trunc(((OPEN_PRICE-PREV_CLOSE)/PREV_CLOSE)*100,2) pct, trunc(((LASTPRICE_N-PREV_CLOSE)/PREV_CLOSE)*100,2) LAST_pct,
  TRUNC((WEIGHT_N)/(BUYER_SELLER_PER_LAKH_N)) WR, WEIGHT_N,CUL_WEIGHT_N,
TRUNC(BUY_SIGNAL_VALUE_N) SPEED, TRUNC(cul_weight_n/(LASTPRICE_N*100),2)PCT_W,
RATIO_F_V,OPEN_PRICE,MAX_PRICE_N,MIN_PRICE_N, PREV_CLOSE ,LASTPRICE_N,
ROUND ((cul_weight_n/100)/ CASE WHEN  (cul_weight_n/LASTPRICE_N)=0 THEN
 .1 ELSE (cul_weight_n/LASTPRICE_N) END ,2) sl_n,LAST_AVG_PRICE_N, 
                 CASE WHEN LAST_AVG_PRICE_N > LASTPRICE_N THEN 
                 'S->'||BUYER_SELLER_PER_LAKH_N
                 ELSE 
                 'B->'||BUYER_SELLER_PER_LAKH_N
                 END   BUYER_SELLER,  A.SYMBOL, TO_CHAR(LAST_UPDATE_DT,'DD-MON-YY HH24:MI') LAST_UPDATE_DT_TIME,  
                 DAILY_BATCH_SEQ_N SEQ_N, SHORT_LONG_FLAG_V, SHORT_LONG_VIR_FLAG_V,BUYER_SELLER_PER_LAKH_N,A.PCT5_TRANS_TOT,TOTALTRADEDVOL_N
FROM DAILY_NSE_MOVEMENT_TRANS_HIST A, FIVEDAYS_AVG_TBL_HIST B WHERE  
 BUYER_SELLER_PER_LAKH_N>0 AND BUYER_SELLER_PER_LAKH_N IS NOT NULL AND 
A.SYMBOL=B.SYMBOL AND  trunc(LAST_UPDATE_DT)=pkg_buysellview_hist.get_date AND TRUNC(RECORD_DATE_DT)=pkg_buysellview_hist.get_date
order by  5 desc,4 desc);
