   SELECT COUNT(*) INTO v_daily_batch_seq_n FROM test_python WHERE symbol=:NEW.symbol AND  last_update_dt 
   BETWEEN TRUNC(SYSDATE) AND SYSDATE;
   
   IF  v_daily_batch_seq_n<>0 THEN 
      :NEW.daily_batch_seq_n:=v_daily_batch_seq_n+1;
    
    BEGIN
    SELECT  ROUND((SYSDATE-last_update_dt)*1440,2),totalTradedVol_n,LASTPRICE_N  
    INTO v_trade_min , v_totalTradedVol_n,V_LASTPRICE_N FROM TEST_PYTHON 
    WHERE SYMBOL=:NEW.symbol AND daily_batch_seq_n=v_daily_batch_seq_n  AND  last_update_dt 
    BETWEEN TRUNC(SYSDATE) AND SYSDATE; ---last time traded volume ....
    EXCEPTION 
    WHEN NO_DATA_FOUND THEN
    SELECT  ROUND((SYSDATE-last_update_dt)*1440,2),totalTradedVol_n,LASTPRICE_N  
    INTO v_trade_min , v_totalTradedVol_n,V_LASTPRICE_N FROM TEST_PYTHON 
    WHERE SYMBOL=:NEW.symbol AND daily_batch_seq_n=v_daily_batch_seq_n-1  AND  last_update_dt 
