        --:NEW.v_trade_min:=v_trade_min;
        --:NEW.LAST_VOLUME_N:=v_totalTradedVol_n;
       SELECT PER_MIN_VOL_N INTO last_10days_per_min_vol  FROM SYMBOL_PERMIN_VOL WHERE symbol= :NEW.symbol;     
        :NEW.per_min_vol_n:= ROUND((:NEW.totalTradedVol_n-v_totalTradedVol_n)/(v_trade_min),2);
     IF :NEW.LASTPRICE_N > V_LASTPRICE_N AND :NEW.per_min_vol_n > 2*(last_10days_per_min_vol) THEN 
          :NEW.BUY_SIGNAL_VALUE_N:=round(:NEW.per_min_vol_n/(last_10days_per_min_vol),2);
          check_symbol_prc(:NEW.symbol,:NEW.BUY_SIGNAL_VALUE_N,:NEW.daily_batch_seq_n);
          ELSE
          :NEW.BUY_SIGNAL_VALUE_N:=0;
     END IF; 
   
    ELSE
    
    :NEW.daily_batch_seq_n:=1;
     ---PER MINUTE VALUME CALCULATION ...
    SELECT (SYSDATE  -TO_DATE(TO_CHAR(SYSDATE,'DDMMYYYY')||'0915','DDMMYYYYHH24:MI'))* 1440 INTO v_trade_min
    FROM DUAL ; 
    ----FIND OUT YESTERDAY CLOSE PRICE 
    BEGIN
    SELECT close_price into l_close_price FROM last_day_nse_all where series='EQ' AND SYMBOL=:NEW.symbol ;
    EXCEPTION WHEN OTHERS THEN 
    NULL;
    l_close_price:=0;
    END;
    :NEW.per_min_vol_n:=round((:NEW.totalTradedVol_n)/(v_trade_min));
    SELECT PER_MIN_VOL_N INTO last_10days_per_min_vol  FROM SYMBOL_PERMIN_VOL WHERE symbol= :NEW.symbol;
       
       IF  :NEW.lastPrice_n > l_close_price AND :NEW.per_min_vol_n > 5*(last_10days_per_min_vol) THEN 
