          check_symbol_prc(:NEW.symbol,:NEW.BUY_SIGNAL_VALUE_N,1);
       END IF;  
          
   END IF;
   
  ----IMPLEMENTED TO AVOID THE REDO GENEARTION --- 
 IF TO_CHAR(SYSDATE,'HH24MI')<1515 THEN
